        $.ajax({
            url: ""/ShoppingCart/Remove"",
            data: { id: pid },
            type: ""post"",
            success: function (response) {
                $("".nn-cart #count"").html(response.Count);
                $(""#amount"").html(response.Amount);
            }
        });
        /*
